mapper.insert:

    SELECT resources_seq.NEXTVAL FROM DUAL

INSERT INTO RESOURCES (
    RESOURCE_ID, RESOURCE_TYPE, RESOURCE_PATTERN, HTTP_METHOD, DESCRIPTION,
    MENU_NAME, MENU_URL, ICON_NAME, PARENT_RESOURCE_ID, DISPLAY_ORDER, IS_GROUP, USE_YN
)
VALUES (
    #{resourceId}, #{resourceType}, #{resourcePattern}, #{httpMethod}, #{description},
    #{menuName}, #{menuUrl}, #{iconName}, #{parentResourceId}, #{displayOrder}, #{isGroup}, #{useYn}
)

mapper.select:
SELECT DISTINCT r.* FROM RESOURCES r JOIN ROLE_RESOURCE_MAPPINGS rrm ON r.RESOURCE_ID = rrm.RESOURCE_ID WHERE r.RESOURCE_TYPE = 'MENU_ITEM' AND r.USE_YN = 'Y' AND rrm.ROLE_ID IN
#{roleId}
ORDER BY r.DISPLAY_ORDER ASC, r.RESOURCE_ID ASC
